and idestudiante = #{idEstudiante} and anio = #{anio}) select ll.id,ll.mes,ll.anio,ll.importe,ll.mora,ll.deuda, (select sum(deuda) from lista l where l.unineg=ll.unineg and l.idestudiante = ll.idestudiante and l.anio=ll.anio) as total from lista ll

