declare @unineg varchar(6),@fechaInicio date,@fechaFin date set @unineg=#{unidadNegocioBean.uniNeg} set @fechaInicio=convert(date,#{fechaInicio}) set @fechaFin=convert(date,#{fechaFin})
select distinct doc.iddocingreso as idDocIngreso ,doc.idcajagen as idCajaGen ,doc.uniNeg as uniNeg ,cod.idCodigo as idTipoDoc ,cod.codigo as codTipoDoc ,doc.serie as serie ,right(replicate('0', 7) + rtrim(doc.nrodoc), 7) as nroDoc ,doc.iddiscente as codDiscente ,doc.discente as nombreDiscente ,doc.anio as anio ,(doc.serie+' - '+right(replicate('0', 7) + rtrim(doc.nrodoc), 7)) as serieNroDoc , doc.fechapago as fechaPago ,lug.idcodigo as idTipoLugarPago ,lug.codigo as lugarPago ,mop.codigo as modoPago ,mop.idcodigo as idTipoModoPago ,doc.tc as tc ,caj.nombre as caja ,isnull(montoefectivosol,0) as montoEfectivoSol ,isnull((montoefectivodol),0) as montoefectivodol ,isnull(montopos1,0) as montoPos1 ,isnull(montopos2,0) as montoPos2 ,(select isnull(sum(isnull(montopagado,0)),0) from MT_DetDocIngreso ddoc where ddoc.iddocingreso=doc.iddocingreso and ddoc.unineg =@unineg) as montoPagado ,sta.idcodigo as idTipoStatusDocIng ,sta.codigo as estadoDoc ,convert(varchar,doc.fechapago,103) as fechaPagoVista , mon.codigo as tipoMoneda ,(select sum(isnull(montoefectivosol,0)+isnull(montopos1,0)+isnull(montopos2,0)) from MT_DocIngreso ddoc where ddoc.iddocingreso=doc.iddocingreso and ddoc.unineg =@unineg) as montoPagadoSol ,(select sum(montoefectivodol) from MT_DocIngreso ddoc where ddoc.iddocingreso=doc.iddocingreso and ddoc.unineg =@unineg) as montoPagadoDol FROM MT_DocIngreso doc INNER JOIN MT_DetDocIngreso det on det.iddocingreso=doc.iddocingreso and doc.unineg=det.unineg LEFT join MT_Concepto con on con.idconcepto = det.idconcepto LEFT JOIN MT_Impresora imp ON imp.impresora = doc.impresora and imp.unineg=doc.unineg LEFT join MT_CajaGen gen on gen.idcajagen = doc.idcajagen and gen.unineg=doc.unineg LEFT JOIN MX_Estudiante est ON est.idestudiante = doc.iddiscente and est.unineg=doc.unineg LEFT JOIN MO_UnidadNegocio uni ON uni.unineg = doc.unineg LEFT join MT_Caja caj on caj.idcaja=doc.idcaja and caj.unineg=doc.unineg LEFT JOIN MO_Codigo cod ON cod.idcodigo = doc.idtipodoc LEFT JOIN MO_Codigo lug ON lug.idcodigo = doc.idtipolugarpago LEFT JOIN MO_Codigo mop ON mop.idcodigo = doc.idtipomodopago LEFT join MO_Codigo sta on sta.idcodigo=doc.idtipostatusdocing LEFT join MO_Codigo mon on mon.idcodigo=doc.idtipomoneda
doc.uniNeg = @unineg and sta.codigo is not null
and doc.idcajagen is not null
and sta.idCodigo = #{tipoStatusDocIng.idCodigo}
and cod.idCodigo = #{idTipoDoc.idCodigo}
and mop.idCodigo = #{idTipoModoPago.idCodigo}
and lug.idCodigo = #{idTipoLugarPago.idCodigo}
and doc.anio = #{anio}
and doc.serie = #{serie}
and doc.nroDoc = #{nroDoc}
and upper(doc.discente) like concat('%',#{nombreDiscente},'%')
and doc.iddiscente =#{codDiscente}
and convert(date,doc.fechaPago) >= @fechaInicio
and convert(date,doc.fechaPago) <= @fechaFin
UNION ALL
