,convert(varchar,doc.creaFechaStatus,103) as fechaPagoVista , mon.codigo as tipoMoneda ,(0) as montoPagadoSol ,(0) as montoPagadoDol FROM MT_DocIngreso doc INNER JOIN MT_DetDocIngreso det on det.iddocingreso=doc.iddocingreso and doc.unineg=det.unineg LEFT join MT_Concepto con on con.idconcepto = det.idconcepto LEFT JOIN MT_Impresora imp ON imp.impresora = doc.impresora and imp.unineg=doc.unineg LEFT join MT_CajaGen gen on gen.idcajagen = doc.idcajagen and gen.unineg=doc.unineg LEFT JOIN MX_Estudiante est ON est.idestudiante = doc.iddiscente and est.unineg=doc.unineg LEFT JOIN MO_UnidadNegocio uni ON uni.unineg = doc.unineg LEFT join MT_Caja caj on caj.idcaja=doc.idcaja and caj.unineg=doc.unineg LEFT JOIN MO_Codigo cod ON cod.idcodigo = doc.idtipodoc LEFT JOIN MO_Codigo lug ON lug.idcodigo = doc.idtipolugarpago LEFT JOIN MO_Codigo mop ON mop.idcodigo = doc.idtipomodopago LEFT join MO_Codigo sta on sta.idcodigo=doc.idtipostatusdocing LEFT join MO_Codigo mon on mon.idcodigo=doc.idtipomoneda
doc.uniNeg = @unineg and isnull(flganulado,0) =1
and doc.idcajagenanulado is not null
and sta.idCodigo = #{tipoStatusDocIng.idCodigo}
