select distinct doc.iddocingreso as idDocIngreso ,doc.idcajagen as idCajaGen ,doc.uniNeg as uniNeg ,cod.idCodigo as idTipoDoc ,cod.codigo as codTipoDoc ,doc.serie as serie ,right(replicate('0', 7) + rtrim(doc.nrodoc), 7) as nroDoc ,doc.iddiscente as codDiscente ,doc.discente as nombreDiscente ,doc.anio as anio ,(doc.serie+' - '+right(replicate('0', 7) + rtrim(doc.nrodoc), 7)) as serieNroDoc ,doc.fechapago as fechaPago ,lug.idcodigo as idTipoLugarPago ,lug.codigo as lugarPago ,mop.codigo as modoPago ,mop.idcodigo as idTipoModoPago ,isnull(montoefectivosol,0) as tc ,caj.nombre as caja ,isnull(montoefectivosol,0) as montoEfectivoSol ,isnull((montoefectivodol),0) as montoefectivodol ,isnull(montopos1,0) as montoPos1 ,isnull(montopos2,0) as montoPos2 ,(select sum(montopagado) from MT_DetDocIngreso ddoc where ddoc.iddocingreso=doc.iddocingreso and ddoc.unineg =#{unidadNegocioBean.uniNeg}) as montoPagado ,sta.idcodigo as idTipoStatusDocIng ,sta.codigo as estadoDoc , (select convert(varchar,doc.fechapago,103)) as fechaPagoVista , mon.codigo as tipoMoneda ,(select sum(isnull(montoefectivosol,0)+isnull(montopos1,0)+isnull(montopos2,0)) from MT_DocIngreso ddoc where ddoc.iddocingreso=doc.iddocingreso and ddoc.unineg =#{unidadNegocioBean.uniNeg}) as montoPagadoSol ,(select sum(montoefectivodol) from MT_DocIngreso ddoc where ddoc.iddocingreso=doc.iddocingreso and ddoc.unineg =#{unidadNegocioBean.uniNeg}) as montoPagadoDol ,cta.mora,cta.fechapago,con.idconcepto,con.nombre as concepto ,mat.seccion ,(case when mat.idgradoacademico = 1 then CONCAT('P','1',mat.seccion) when mat.idgradoacademico = 2 then CONCAT('P','2',mat.seccion) when mat.idgradoacademico = 3 then CONCAT('P','3',mat.seccion) when mat.idgradoacademico = 4 then CONCAT('P','4',mat.seccion) when mat.idgradoacademico = 5 then CONCAT('P','5',mat.seccion) when mat.idgradoacademico = 6 then CONCAT('P','6',mat.seccion)
when mat.idgradoacademico = 7 then CONCAT('S','1',mat.seccion) when mat.idgradoacademico = 8 then CONCAT('S','2',mat.seccion) when mat.idgradoacademico = 9 then CONCAT('S','3',mat.seccion) when mat.idgradoacademico = 10 then CONCAT('S','4',mat.seccion) when mat.idgradoacademico = 11 then CONCAT('S','5',mat.seccion)
when mat.idgradoacademico = 23 then CONCAT('I','3',mat.seccion) when mat.idgradoacademico = 24 then CONCAT('I','4',mat.seccion) when mat.idgradoacademico = 25 then CONCAT('I','5',mat.seccion) end) as grado ,mat.anio as anioRep ,(case when doc.flgmasivo is null then 'O' when doc.flgmasivo is not null then 'C' end) as flgmasivo, est.codigo as codigo, (case when month(cta.fechapago) = 2 then 'MATRICULA' when month(cta.fechapago) = 3 then 'MARZO' when month(cta.fechapago) = 4 then 'ABRIL' when month(cta.fechapago) = 5 then 'MAYO' when month(cta.fechapago) = 6 then 'JUNIO' when month(cta.fechapago) = 7 then 'JULIO' when month(cta.fechapago) = 8 then 'AGOSTO' when month(cta.fechapago) = 9 then 'SETIEMBRE' when month(cta.fechapago) = 10 then 'OCTUBRE' when month(cta.fechapago) = 11 then 'NOVIEMBRE' when month(cta.fechapago) = 12 then 'DICIEMBRE' end) as mesVista FROM MT_DocIngreso doc INNER JOIN MT_DetDocIngreso det on det.iddocingreso=doc.iddocingreso and doc.unineg=det.unineg inner join MT_CtasxCobrar cta on doc.iddocingreso = cta.iddocingreso and cta.unineg = doc.unineg inner join MX_Matricula mat on cta.idmatricula = mat.idmatricula and mat.unineg = cta.unineg
inner join MT_ProcesoBanco pb on cta.idprocesobanco = pb.idprocesobanco
LEFT join MT_Concepto con on con.idconcepto = det.idconcepto LEFT JOIN MT_Impresora imp ON imp.impresora = doc.impresora and imp.unineg=doc.unineg left join MT_CajaGen gen on gen.idcajagen = doc.idcajagen and gen.unineg=doc.unineg LEFT JOIN MX_Estudiante est ON est.idestudiante = doc.iddiscente and est.unineg=doc.unineg LEFT JOIN MO_UnidadNegocio uni ON uni.unineg = doc.unineg LEFT join MT_Caja caj on caj.idcaja=doc.idcaja and caj.unineg=doc.unineg LEFT JOIN MO_Codigo cod ON cod.idcodigo = doc.idtipodoc LEFT JOIN MO_Codigo lug ON lug.idcodigo = doc.idtipolugarpago LEFT JOIN MO_Codigo mop ON mop.idcodigo = doc.idtipomodopago LEFT join MO_Codigo sta on sta.idcodigo=doc.idtipostatusdocing LEFT join MO_Codigo mon on mon.idcodigo=doc.idtipomoneda
doc.uniNeg = #{unidadNegocioBean.uniNeg}
and doc.idtipolugarpago = #{idTipoLugarPago.idCodigo}
and doc.idcajagen is not null and SUBSTRING(CONVERT(varchar,con.idconcepto),1,3) in (100,101)
and cta.idprocesobanco is not null
and sta.idCodigo = #{tipoStatusDocIng.idCodigo}
and cod.idCodigo = #{idTipoDoc.idCodigo}
and mop.idCodigo = #{idTipoModoPago.idCodigo}
and lug.idCodigo = #{idTipoLugarPago.idCodigo}
and upper(doc.discente) like concat('%',#{nombreDiscente},'%')
and doc.iddiscente =#{codDiscente}
and (case when doc.creaStatus is null then doc.fechaPago else doc.creaFechaStatus end) >= #{fechaInicio}
and (case when doc.creaStatus is null then doc.fechaPago else doc.creaFechaStatus end) <= #{fechaFin}
order by doc.fechaPago

